INSERT INTO `wp_h3c0_snippets` VALUES (1,'Example HTML shortcode','This is an example snippet for demonstrating how to add an HTML shortcode.\n\nYou can remove it, or edit it to add your own content.','\nadd_shortcode( \'shortcode_name\', function () {\n\n	$out = \'<p>write your HTML shortcode content here</p>\';\n\n	return $out;\n} );','shortcode','global',10,0,'2021-03-27 16:00:45'),(2,'Example CSS snippet','This is an example snippet for demonstrating how to add custom CSS code to your website.\n\nYou can remove it, or edit it to add your own content.','\nadd_action( \'wp_head\', function () { ?>\n<style>\n\n	/* write your CSS code here */\n\n</style>\n<?php } );\n','css','front-end',10,0,'2021-03-27 16:00:45'),(3,'Example JavaScript snippet','This is an example snippet for demonstrating how to add custom JavaScript code to your website.\n\nYou can remove it, or edit it to add your own content.','\nadd_action( \'wp_head\', function () { ?>\n<script>\n\n	/* write your JavaScript code here */\n\n</script>\n<?php } );\n','javascript','front-end',10,0,'2021-03-27 16:00:45'),(4,'Order snippets by name','Order snippets by name by default in the snippets table.','\nadd_filter( \'code_snippets/list_table/default_orderby\', function () {\n	return \'name\';\n} );\n','code-snippets-plugin','admin',10,0,'2021-03-27 16:00:45'),(5,'Order snippets by date','Order snippets by last modification date by default in the snippets table.','\nadd_filter( \'code_snippets/list_table/default_orderby\', function () {\n	return \'modified\';\n} );\n\nadd_filter( \'code_snippets/list_table/default_order\', function () {\n	return \'desc\';\n} );\n','code-snippets-plugin','admin',10,0,'2021-03-27 16:00:45'),(6,'Post-purchase redirect','','/* Redirect user after check out*/\r\nadd_action(\'template_redirect\',\'Id_custom_redirect_after_purchase\');\r\n\r\nfunction Id_custom_redirect_after_purchase(){\r\n	\r\n	global $wp;\r\n	\r\n	if(is_checkout() && ! empty($wp->query_vars[\'order-received\'])){\r\n		wp_redirect(\'https://woocommerce-509196-1691061.cloudwaysapps.com/mis-cursos/\');\r\n		exit;\r\n	}\r\n	\r\n}','','front-end',10,1,'2021-03-27 16:09:59'),(7,'WooCommerce Product Reviews Shortcode','<h3 class=\"entry-title\" style=\"box-sizing: border-box; font-family: montserrat, sans-serif; line-height: 1.3; color: #20232c; margin: 30px 0px; font-size: 2.25rem; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; vertical-align: baseline; text-rendering: optimizelegibility; -webkit-font-smoothing: antialiased;\">Display WooCommerce Product Reviews on Custom Page</h3>','/**\r\n * WooCommerce Product Reviews Shortcode\r\n */\r\n \r\nadd_shortcode( \'product_reviews\', \'silva_product_reviews_shortcode\' );\r\n \r\nfunction silva_product_reviews_shortcode( $atts ) {\r\n    \r\n   if ( empty( $atts ) ) return \'\';\r\n \r\n   if ( ! isset( $atts[\'id\'] ) ) return \'\';\r\n       \r\n   $comments = get_comments( \'post_id=\' . $atts[\'id\'] );\r\n    \r\n   if ( ! $comments ) return \'\';\r\n    \r\n   $html .= \'<div class=\"woocommerce-tabs\"><div id=\"reviews\"><ol class=\"commentlist\">\';\r\n    \r\n   foreach ( $comments as $comment ) {   \r\n      $rating = intval( get_comment_meta( $comment->comment_ID, \'rating\', true ) );\r\n      $html .= \'<li class=\"review\">\';\r\n      $html .= get_avatar( $comment, \'60\' );\r\n      $html .= \'<div class=\"comment-text\">\';\r\n      if ( $rating ) $html .= wc_get_rating_html( $rating );\r\n      $html .= \'<p class=\"meta\"><strong class=\"woocommerce-review__author\">\';\r\n      $html .= get_comment_author( $comment );\r\n      $html .= \'</strong></p>\';\r\n      $html .= \'<div class=\"description\">\';\r\n      $html .= $comment->comment_content;\r\n      $html .= \'</div></div>\';\r\n      $html .= \'</li>\';\r\n   }\r\n    \r\n   $html .= \'</ol></div></div>\';\r\n    \r\n   return $html;\r\n}','shortcode','front-end',10,1,'2021-05-21 22:28:31');
